@Pipe({
    name: 'rareteColor'
  })
  export class RareteColorPipe implements PipeTransform {
    transform(rarete: string): string {
      switch (rarete) {
        case 'commun':
          return 'rgb(0, 0, 0)';
        case 'rare':
          return 'rgb(240, 93, 48)';
        case 'légendaire':
          return 'rgb(255, 215, 0)';
        default:
          return 'rgb(0, 0, 0)';
      }
    }
  }
