import { Component, OnInit } from '@angular/core';

import { POKEMONS } from '../donnees-pokemons/mock-pokemon';
import { Pokemon } from '../donnees-pokemons/pokemon';
//Importation d'angular le router pour les liens
import { Router } from '@angular/router';
import { PokemonService } from 'src/app/pokemon.service';

@Component({
  selector: 'list-pokemon',
  templateUrl: './pokemons.component.html',
  styleUrls: ['./pokemons.component.css'],
})
export class PokemonsComponent implements OnInit {
//   pokemons!: Pokemon[];
//   pokemonTab !: Pokemon[];
//   pokemonData!: string;

  pokemons !: Pokemon[];
  pokemonSearch !: Pokemon[];
  search !: string;
  pokemonS !: any;

  constructor(private router: Router, private pokemonsService: PokemonService) {}

  ngOnInit(): void {
    //J'insère les données de mock-pokemon.ts dans la variable pokemons du composant
    // this.pokemons = this.pokemonService.getPokemon()
    // this.pokemons = POKEMONS;

    this.pokemonsService
      .getPokemons()
      .subscribe((pokemons) => (this.pokemons = pokemons));

      this.pokemonsService.getPokemons().subscribe(
        pokemons => this.pokemonSearch = pokemons
    )
  }

  selectPokemon(pokemon: Pokemon) {
    console.log(pokemon.id, 'clickd');
    let link = ['/pokemon', pokemon.id];
    this.router.navigate(link)
  }

//   pokemonSearch() {
//     this.pokemonData = this.pokemonData.toLowerCase();
//   if (this.pokemonData == "") {
//     this.pokemonTab = [];
//     this.pokemonsService.getPokemons().subscribe(
//       pokemons => this.pokemonTab = pokemons
//     )
//   } else {
//     this.pokemonTab = this.pokemons.filter(pokemon => 
//       pokemon.name.toLowerCase().includes(this.pokemonData) || 
//       pokemon.rarete == this.pokemonData ||
//       pokemon.types.some(type => type.toLowerCase().includes(this.pokemonData))
//     );
//   }
//   console.log("xorkd")
// }

    public myString = this.pokemons


searchPokemon(){
    this.search = this.search.toLowerCase();
    if (this.search == "") {
        this.pokemonSearch = [];
        this.pokemonsService.getPokemons().subscribe(
            pokemons => this.pokemonSearch = pokemons
        )
    } else{
        this.pokemonSearch = [];
        for (let i = 0; i < this.pokemons.length; i++) {
            if (this.pokemons[i].name.toLowerCase().includes(this.search)) {
                this.pokemonSearch.push(this.pokemons[i]);
            }else{
                if (this.pokemons[i].rarity == this.search) {
                    this.pokemonSearch.push(this.pokemons[i]);
                }
                else{
                    let alreadyIn = false;
                    for (let j = 0; j < this.pokemons[i].types.length; j++) {
                        if (this.pokemons[i].types[j].toLowerCase().includes(this.search) && !alreadyIn) {
                            this.pokemonSearch.push(this.pokemons[i]);
                            alreadyIn = true;
                        }
                    }                
                }
            }
        }
    }
}

}
